import React, { useState, useEffect } from 'react';
import api from '../api';
import { Container, Table, Badge, Button, Alert, Spinner, Modal } from 'react-bootstrap';

function OrderList() {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [selectedOrder, setSelectedOrder] = useState(null);
  const [showDetails, setShowDetails] = useState(false);

  useEffect(() => {
    fetchOrders();
  }, []);

  const fetchOrders = async () => {
    try {
      setLoading(true);
      const response = await api.get('/orders/my-orders');
      setOrders(response.data);
      setError(null);
    } catch (err) {
      console.error('Error fetching orders:', err);
      setError('Failed to load orders. Please try again later.');
    } finally {
      setLoading(false);
    }
  };

  const getStatusBadgeVariant = (status) => {
    switch (status.toLowerCase()) {
      case 'pending': return 'warning';
      case 'confirmed': return 'info';
      case 'shipped': return 'primary';
      case 'delivered': return 'success';
      case 'cancelled': return 'danger';
      default: return 'secondary';
    }
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  const handleViewDetails = (order) => {
    setSelectedOrder(order);
    setShowDetails(true);
  };

  if (loading) {
    return (
      <div className="text-center">
        <Spinner animation="border" role="status">
          <span className="visually-hidden">Loading orders...</span>
        </Spinner>
        <p className="mt-2">Loading your orders...</p>
      </div>
    );
  }

  if (error) {
    return (
      <Alert variant="danger">
        <Alert.Heading>Error</Alert.Heading>
        <p>{error}</p>
        <Button variant="outline-danger" onClick={fetchOrders}>
          Try Again
        </Button>
      </Alert>
    );
  }

  return (
    <Container>
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h2>My Orders</h2>
        <Button variant="primary" onClick={fetchOrders}>
          Refresh
        </Button>
      </div>

      {orders.length === 0 ? (
        <Alert variant="info">
          <Alert.Heading>No Orders Found</Alert.Heading>
          <p>You haven't placed any orders yet.</p>
          <Button variant="primary" href="#create-order">
            Create Your First Order
          </Button>
        </Alert>
      ) : (
        <Table responsive striped bordered hover>
          <thead className="table-dark">
            <tr>
              <th>Order ID</th>
              <th>Date</th>
              <th>Status</th>
              <th>Total Amount</th>
              <th>Items</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {orders.map((order) => (
              <tr key={order.id}>
                <td>#{order.id}</td>
                <td>{formatDate(order.orderDate)}</td>
                <td>
                  <Badge bg={getStatusBadgeVariant(order.status)}>
                    {order.status}
                  </Badge>
                </td>
                <td className="fw-bold text-success">${order.totalAmount}</td>
                <td>{order.items?.length || 0} item(s)</td>
                <td>
                  <Button
                    variant="outline-primary"
                    size="sm"
                    onClick={() => handleViewDetails(order)}
                  >
                    View Details
                  </Button>
                </td>
              </tr>
            ))}
          </tbody>
        </Table>
      )}

      {/* Order Details Modal */}
      <Modal show={showDetails} onHide={() => setShowDetails(false)} size="lg" centered>
        <Modal.Header closeButton className="bg-light">
          <Modal.Title className="d-flex align-items-center">
            <span style={{ fontSize: '1.5rem', marginRight: '0.5rem' }}>üìã</span>
            Order Details #{selectedOrder?.id}
          </Modal.Title>
        </Modal.Header>
        <Modal.Body className="p-4">
          {selectedOrder && (
            <div>
              {/* Order Summary Cards */}
              <Row className="mb-4">
                <Col md={6}>
                  <Card className="text-center border-0 shadow-sm">
                    <Card.Body>
                      <div style={{ fontSize: '2rem', marginBottom: '0.5rem' }}>üìÖ</div>
                      <h6 className="text-muted mb-1">Order Date</h6>
                      <p className="mb-0 fw-bold">{formatDate(selectedOrder.orderDate)}</p>
                    </Card.Body>
                  </Card>
                </Col>
                <Col md={6}>
                  <Card className="text-center border-0 shadow-sm">
                    <Card.Body>
                      <div style={{ fontSize: '2rem', marginBottom: '0.5rem' }}>
                        {selectedOrder.status === 'DELIVERED' ? '‚úÖ' :
                         selectedOrder.status === 'SHIPPED' ? 'üöö' :
                         selectedOrder.status === 'CONFIRMED' ? 'üìã' :
                         selectedOrder.status === 'PENDING' ? '‚è≥' : '‚ùå'}
                      </div>
                      <h6 className="text-muted mb-1">Status</h6>
                      <Badge bg={getStatusBadgeVariant(selectedOrder.status)} className="fs-6 px-3 py-2">
                        {selectedOrder.status}
                      </Badge>
                    </Card.Body>
                  </Card>
                </Col>
              </Row>

              {/* Additional Info */}
              <Row className="mb-4">
                <Col md={6}>
                  <Card className="border-0 shadow-sm">
                    <Card.Body>
                      <h6 className="text-muted mb-1">üë§ User ID</h6>
                      <p className="mb-0 fw-bold">{selectedOrder.userId}</p>
                    </Card.Body>
                  </Card>
                </Col>
                <Col md={6}>
                  <Card className="border-0 shadow-sm">
                    <Card.Body>
                      <h6 className="text-muted mb-1">üí∞ Total Amount</h6>
                      <p className="mb-0 fw-bold text-success fs-5">${selectedOrder.totalAmount}</p>
                    </Card.Body>
                  </Card>
                </Col>
              </Row>

              {/* Order Items */}
              <h5 className="mb-3 d-flex align-items-center">
                <span style={{ fontSize: '1.2rem', marginRight: '0.5rem' }}>üõí</span>
                Order Items ({selectedOrder.items?.length || 0})
              </h5>

              <div className="mb-3">
                {selectedOrder.items?.map((item, index) => (
                  <Card key={index} className="mb-3 border-0 shadow-sm">
                    <Card.Body className="py-3">
                      <Row className="align-items-center">
                        <Col md={6}>
                          <h6 className="mb-1">{item.productName}</h6>
                          <small className="text-muted">${item.price} each</small>
                        </Col>
                        <Col md={3} className="text-center">
                          <Badge bg="secondary" className="fs-6 px-3 py-2">
                            Qty: {item.quantity}
                          </Badge>
                        </Col>
                        <Col md={3} className="text-end">
                          <span className="fw-bold text-success fs-5">
                            ${(item.price * item.quantity).toFixed(2)}
                          </span>
                        </Col>
                      </Row>
                    </Card.Body>
                  </Card>
                ))}
              </div>

              {/* Total Summary */}
              <Card className="border-success shadow-lg" style={{ borderWidth: '2px' }}>
                <Card.Body className="text-center py-4">
                  <h4 className="text-success mb-0">
                    üí∞ Grand Total: ${selectedOrder.totalAmount}
                  </h4>
                </Card.Body>
              </Card>
            </div>
          )}
        </Modal.Body>
        <Modal.Footer className="bg-light">
          <Button variant="secondary" onClick={() => setShowDetails(false)}>
            Close
          </Button>
          <Button
            variant="primary"
            onClick={() => {
              setShowDetails(false);
              showToastMessage('Order details viewed successfully!');
            }}
          >
            üëç Got it
          </Button>
        </Modal.Footer>
      </Modal>

      {/* Toast Notifications */}
      <ToastContainer position="top-end" className="p-3">
        <Toast show={showToast} onClose={() => setShowToast(false)} delay={3000} autohide>
          <Toast.Header>
            <span style={{ fontSize: '1.2rem' }}>üìã</span>
            <strong className="me-auto ms-2">Order Update</strong>
          </Toast.Header>
          <Toast.Body>{toastMessage}</Toast.Body>
        </Toast>
      </ToastContainer>

      <style jsx>{`
        @keyframes slideInLeft {
          from {
            opacity: 0;
            transform: translateX(-30px);
          }
          to {
            opacity: 1;
            transform: translateX(0);
          }
        }
      `}</style>
    </Container>
  );
}

export default OrderList;
