import React, { useState, useEffect } from 'react';
import api from '../api';
import { Container, Table, Badge, Button, Alert, Spinner, Modal } from 'react-bootstrap';

function OrderList() {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [selectedOrder, setSelectedOrder] = useState(null);
  const [showDetails, setShowDetails] = useState(false);

  useEffect(() => {
    fetchOrders();
  }, []);

  const fetchOrders = async () => {
    try {
      setLoading(true);
      const response = await api.get('/orders/my-orders');
      setOrders(response.data);
      setError(null);
    } catch (err) {
      console.error('Error fetching orders:', err);
      setError('Failed to load orders. Please try again later.');
    } finally {
      setLoading(false);
    }
  };

  const getStatusBadgeVariant = (status) => {
    switch (status.toLowerCase()) {
      case 'pending': return 'warning';
      case 'confirmed': return 'info';
      case 'shipped': return 'primary';
      case 'delivered': return 'success';
      case 'cancelled': return 'danger';
      default: return 'secondary';
    }
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  const handleViewDetails = (order) => {
    setSelectedOrder(order);
    setShowDetails(true);
  };

  if (loading) {
    return (
      <div className="text-center">
        <Spinner animation="border" role="status">
          <span className="visually-hidden">Loading orders...</span>
        </Spinner>
        <p className="mt-2">Loading your orders...</p>
      </div>
    );
  }

  if (error) {
    return (
      <Alert variant="danger">
        <Alert.Heading>Error</Alert.Heading>
        <p>{error}</p>
        <Button variant="outline-danger" onClick={fetchOrders}>
          Try Again
        </Button>
      </Alert>
    );
  }

  return (
    <Container fluid className="py-4" style={{ background: 'linear-gradient(135deg, #f093fb 0%, #f5576c 100%)', minHeight: '100vh' }}>
      <Container>
        {/* Header Section */}
        <div className="text-center mb-5">
          <h1 className="display-4 fw-bold text-white mb-3" style={{ textShadow: '2px 2px 4px rgba(0,0,0,0.3)' }}>
            ðŸ“‹ My Orders
          </h1>
          <p className="lead text-white-50 mb-4">
            Track and manage your order history
          </p>

          {/* Search and Filter */}
          <Row className="mb-4 justify-content-center">
            <Col md={6} lg={4} className="mb-3">
              <InputGroup className="shadow-lg">
                <InputGroup.Text className="bg-white border-0">
                  <span style={{ fontSize: '1.2rem' }}>ðŸ”</span>
                </InputGroup.Text>
                <Form.Control
                  type="text"
                  placeholder="Search orders..."
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                  className="border-0 bg-white"
                />
              </InputGroup>
            </Col>
            <Col md={6} lg={3} className="mb-3">
              <Form.Select
                value={statusFilter}
                onChange={(e) => setStatusFilter(e.target.value)}
                className="shadow-lg bg-white border-0"
                style={{ height: '50px', fontSize: '1.1rem' }}
              >
                <option value="ALL">All Status</option>
                <option value="PENDING">Pending</option>
                <option value="CONFIRMED">Confirmed</option>
                <option value="SHIPPED">Shipped</option>
                <option value="DELIVERED">Delivered</option>
                <option value="CANCELLED">Cancelled</option>
              </Form.Select>
            </Col>
          </Row>

          {/* Stats Cards */}
          <Row className="mb-4">
            <Col md={3}>
              <Card className="bg-white bg-opacity-10 text-white border-0 shadow">
                <Card.Body className="text-center py-3">
                  <div style={{ fontSize: '2rem' }}>ðŸ“¦</div>
                  <h4 className="mb-1">{orders.length}</h4>
                  <small>Total Orders</small>
                </Card.Body>
              </Card>
            </Col>
            <Col md={3}>
              <Card className="bg-white bg-opacity-10 text-white border-0 shadow">
                <Card.Body className="text-center py-3">
                  <div style={{ fontSize: '2rem' }}>â³</div>
                  <h4 className="mb-1">{orders.filter(o => o.status === 'PENDING').length}</h4>
                  <small>Pending</small>
                </Card.Body>
              </Card>
            </Col>
            <Col md={3}>
              <Card className="bg-white bg-opacity-10 text-white border-0 shadow">
                <Card.Body className="text-center py-3">
                  <div style={{ fontSize: '2rem' }}>ðŸšš</div>
                  <h4 className="mb-1">{orders.filter(o => ['SHIPPED', 'DELIVERED'].includes(o.status)).length}</h4>
                  <small>In Transit</small>
                </Card.Body>
              </Card>
            </Col>
            <Col md={3}>
              <Card className="bg-white bg-opacity-10 text-white border-0 shadow">
                <Card.Body className="text-center py-3">
                  <div style={{ fontSize: '2rem' }}>âœ…</div>
                  <h4 className="mb-1">{orders.filter(o => o.status === 'DELIVERED').length}</h4>
                  <small>Delivered</small>
                </Card.Body>
              </Card>
            </Col>
          </Row>
        </div>

        {/* Loading State */}
        {loading && (
          <div className="text-center py-5">
            <Spinner animation="border" variant="light" size="lg" className="mb-3" />
            <h5 className="text-white">Loading your orders...</h5>
          </div>
        )}

        {/* Error State */}
        {error && (
          <Alert variant="danger" className="shadow-lg">
            <Alert.Heading>âŒ Oops! Something went wrong</Alert.Heading>
            <p>{error}</p>
            <Button variant="outline-danger" onClick={fetchOrders}>
              ðŸ”„ Try Again
            </Button>
          </Alert>
        )}

        {/* Orders Grid */}
        {!loading && !error && (
          <>
            {filteredOrders.length === 0 ? (
              <Card className="bg-white shadow-lg text-center py-5">
                <Card.Body>
                  <div style={{ fontSize: '4rem', marginBottom: '1rem' }}>ðŸ“­</div>
                  <h3 className="text-muted mb-3">
                    {searchTerm || statusFilter !== 'ALL' ? 'No orders found' : 'No Orders Yet'}
                  </h3>
                  <p className="text-muted mb-4">
                    {searchTerm || statusFilter !== 'ALL'
                      ? 'Try adjusting your search or filter criteria'
                      : "You haven't placed any orders yet. Start shopping now!"
                    }
                  </p>
                  {(searchTerm || statusFilter !== 'ALL') && (
                    <div className="mb-3">
                      <Button
                        variant="outline-primary"
                        onClick={() => {
                          setSearchTerm('');
                          setStatusFilter('ALL');
                        }}
                        className="me-2"
                      >
                        Clear Filters
                      </Button>
                    </div>
                  )}
                  <Button
                    variant="primary"
                    size="lg"
                    href="#products"
                    disabled={loading}
                  >
                    ðŸ›ï¸ Start Shopping
                  </Button>
                </Card.Body>
              </Card>
            ) : (
              <>
                <div className="d-flex justify-content-between align-items-center mb-4">
                  <h5 className="text-white mb-0">
                    Showing {filteredOrders.length} of {orders.length} orders
                  </h5>
                  <Button
                    variant="light"
                    onClick={fetchOrders}
                    disabled={loading}
                  >
                    ðŸ”„ Refresh
                  </Button>
                </div>

                <Row>
                  {filteredOrders.map((order, index) => (
                    <Col key={order.id} xl={4} lg={6} className="mb-4">
                      <Card
                        className="h-100 shadow-lg border-0 overflow-hidden"
                        style={{
                          background: 'white',
                          transition: 'all 0.3s ease',
                          animation: `slideInLeft 0.5s ease-out ${index * 0.1}s both`
                        }}
                        onMouseEnter={(e) => {
                          e.currentTarget.style.transform = 'translateY(-5px)';
                          e.currentTarget.style.boxShadow = '0 10px 25px rgba(0,0,0,0.15)';
                        }}
                        onMouseLeave={(e) => {
                          e.currentTarget.style.transform = 'translateY(0)';
