                <Col md={6}>
                  <Card className="border-0 shadow-sm">
                    <Card.Body>
                      <h6 className="text-muted mb-1">ðŸ’° Total Amount</h6>
                      <p className="mb-0 fw-bold text-success fs-5">${selectedOrder.totalAmount}</p>
