import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Container, Row, Col, Card, Badge, Button, Alert, Spinner, Modal, InputGroup, Form, Toast, ToastContainer } from 'react-bootstrap';

const API_BASE_URL = 'http://localhost:8087/api';

function OrderList() {
  const [orders, setOrders] = useState([]);
  const [filteredOrders, setFilteredOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [selectedOrder, setSelectedOrder] = useState(null);
  const [showDetails, setShowDetails] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');
  const [statusFilter, setStatusFilter] = useState('ALL');
  const [showToast, setShowToast] = useState(false);
  const [toastMessage, setToastMessage] = useState('');

  useEffect(() => {
    fetchOrders();
  }, []);

  useEffect(() => {
    // Filter orders based on search term and status
    let filtered = orders;

    if (statusFilter !== 'ALL') {
      filtered = filtered.filter(order => order.status === statusFilter);
    }

    if (searchTerm) {
      filtered = filtered.filter(order =>
        order.id.toString().toLowerCase().includes(searchTerm.toLowerCase()) ||
        order.status.toLowerCase().includes(searchTerm.toLowerCase())
      );
    }

    setFilteredOrders(filtered);
  }, [orders, searchTerm, statusFilter]);

  const fetchOrders = async () => {
    try {
      setLoading(true);
      const response = await axios.get(`${API_BASE_URL}/orders/my-orders`);
      setOrders(response.data);
      setError(null);
    } catch (err) {
      console.error('Error fetching orders:', err);
      setError('Failed to load orders. Please try again later.');
    } finally {
      setLoading(false);
    }
  };

  const showToastMessage = (message) => {
    setToastMessage(message);
    setShowToast(true);
  };

  const getStatusBadgeVariant = (status) => {
    switch (status.toLowerCase()) {
      case 'pending': return 'warning';
      case 'confirmed': return 'info';
      case 'shipped': return 'primary';
      case 'delivered': return 'success';
      case 'cancelled': return 'danger';
      default: return 'secondary';
    }
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  const handleViewDetails = (order) => {
    setSelectedOrder(order);
    setShowDetails(true);
  };

  if (loading) {
    return (
      <div className="text-center">
        <Spinner animation="border" role="status">
          <span className="visually-hidden">Loading orders...</span>
        </Spinner>
        <p className="mt-2">Loading your orders...</p>
      </div>
    );
  }

  if (error) {
    return (
      <Alert variant="danger">
        <Alert.Heading>Error</Alert.Heading>
        <p>{error}</p>
        <Button variant="outline-danger" onClick={fetchOrders}>
          Try Again
        </Button>
      </Alert>
    );
  }

  return (
    <Container fluid className="py-4" style={{ background: 'linear-gradient(135deg, #f093fb 0%, #f5576c 100%)', minHeight: '100vh' }}>
      <Container>
        {/* Header Section */}
        <div className="text-center mb-5">
          <h1 className="display-4 fw-bold text-white mb-3" style={{ textShadow: '2px 2px 4px rgba(0,0,0,0.3)' }}>
            üìã My Orders
          </h1>
          <p className="lead text-white-50 mb-4">
            Track and manage your order history
          </p>

          {/* Search and Filter */}
          <Row className="mb-4 justify-content-center">
            <Col md={6} lg={4} className="mb-3">
              <InputGroup className="shadow-lg">
                <InputGroup.Text className="bg-white border-0">
                  <span style={{ fontSize: '1.2rem' }}>üîç</span>
                </InputGroup.Text>
                <Form.Control
                  type="text"
                  placeholder="Search orders..."
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                  className="border-0 bg-white"
                />
              </InputGroup>
            </Col>
            <Col md={6} lg={3} className="mb-3">
              <Form.Select
                value={statusFilter}
                onChange={(e) => setStatusFilter(e.target.value)}
                className="shadow-lg bg-white border-0"
                style={{ height: '50px', fontSize: '1.1rem' }}
              >
                <option value="ALL">All Status</option>
                <option value="PENDING">Pending</option>
                <option value="CONFIRMED">Confirmed</option>
                <option value="SHIPPED">Shipped</option>
                <option value="DELIVERED">Delivered</option>
                <option value="CANCELLED">Cancelled</option>
              </Form.Select>
            </Col>
          </Row>

          {/* Stats Cards */}
          <Row className="mb-4">
            <Col md={3}>
              <Card className="bg-white bg-opacity-10 text-white border-0 shadow">
                <Card.Body className="text-center py-3">
                  <div style={{ fontSize: '2rem' }}>üì¶</div>
                  <h4 className="mb-1">{orders.length}</h4>
                  <small>Total Orders</small>
                </Card.Body>
              </Card>
            </Col>
            <Col md={3}>
              <Card className="bg-white bg-opacity-10 text-white border-0 shadow">
                <Card.Body className="text-center py-3">
                  <div style={{ fontSize: '2rem' }}>‚è≥</div>
                  <h4 className="mb-1">{orders.filter(o => o.status === 'PENDING').length}</h4>
                  <small>Pending</small>
                </Card.Body>
              </Card>
            </Col>
            <Col md={3}>
              <Card className="bg-white bg-opacity-10 text-white border-0 shadow">
                <Card.Body className="text-center py-3">
                  <div style={{ fontSize: '2rem' }}>üöö</div>
                  <h4 className="mb-1">{orders.filter(o => ['SHIPPED', 'DELIVERED'].includes(o.status)).length}</h4>
                  <small>In Transit</small>
                </Card.Body>
              </Card>
            </Col>
            <Col md={3}>
              <Card className="bg-white bg-opacity-10 text-white border-0 shadow">
                <Card.Body className="text-center py-3">
                  <div style={{ fontSize: '2rem' }}>‚úÖ</div>
                  <h4 className="mb-1">{orders.filter(o => o.status === 'DELIVERED').length}</h4>
                  <small>Delivered</small>
                </Card.Body>
              </Card>
            </Col>
          </Row>
        </div>

        {/* Loading State */}
        {loading && (
          <div className="text-center py-5">
            <Spinner animation="border" variant="light" size="lg" className="mb-3" />
            <h5 className="text-white">Loading your orders...</h5>
          </div>
        )}

        {/* Error State */}
        {error && (
          <Alert variant="danger" className="shadow-lg">
            <Alert.Heading>‚ùå Oops! Something went wrong</Alert.Heading>
            <p>{error}</p>
            <Button variant="outline-danger" onClick={fetchOrders}>
              üîÑ Try Again
            </Button>
          </Alert>
        )}

        {/* Orders Grid */}
        {!loading && !error && (
          <>
            {filteredOrders.length === 0 ? (
              <Card className="bg-white shadow-lg text-center py-5">
                <Card.Body>
                  <div style={{ fontSize: '4rem', marginBottom: '1rem' }}>üì≠</div>
                  <h3 className="text-muted mb-3">
                    {searchTerm || statusFilter !== 'ALL' ? 'No orders found' : 'No Orders Yet'}
                  </h3>
                  <p className="text-muted mb-4">
                    {searchTerm || statusFilter !== 'ALL'
                      ? 'Try adjusting your search or filter criteria'
                      : "You haven't placed any orders yet. Start shopping now!"
                    }
                  </p>
                  {(searchTerm || statusFilter !== 'ALL') && (
                    <div className="mb-3">
                      <Button
                        variant="outline-primary"
                        onClick={() => {
                          setSearchTerm('');
                          setStatusFilter('ALL');
                        }}
                        className="me-2"
                      >
                        Clear Filters
                      </Button>
                    </div>
                  )}
                  <Button
                    variant="primary"
                    size="lg"
                    href="#products"
                    disabled={loading}
                  >
                    üõçÔ∏è Start Shopping
                  </Button>
                </Card.Body>
              </Card>
            ) : (
              <>
                <div className="d-flex justify-content-between align-items-center mb-4">
                  <h5 className="text-white mb-0">
                    Showing {filteredOrders.length} of {orders.length} orders
                  </h5>
                  <Button
                    variant="light"
                    onClick={fetchOrders}
                    disabled={loading}
                  >
                    üîÑ Refresh
                  </Button>
                </div>

                <Row>
                  {filteredOrders.map((order, index) => (
                    <Col key={order.id} xl={4} lg={6} className="mb-4">
                      <Card
                        className="h-100 shadow-lg border-0 overflow-hidden"
                        style={{
                          background: 'white',
                          transition: 'all 0.3s ease',
                          animation: `slideInLeft 0.5s ease-out ${index * 0.1}s both`
                        }}
                        onMouseEnter={(e) => {
                          e.currentTarget.style.transform = 'translateY(-5px)';
                          e.currentTarget.style.boxShadow = '0 10px 25px rgba(0,0,0,0.15)';
                        }}
                        onMouseLeave={(e) => {
                          e.currentTarget.style.transform = 'translateY(0)';
                          e.currentTarget.style.boxShadow = '0 5px 15px rgba(0,0,0,0.08)';
                        }}
                      >
                        {/* Status Header */}
                        <div
                          className="position-relative"
                          style={{
                            height: '80px',
                            background: `linear-gradient(45deg, ${
                              order.status === 'DELIVERED' ? '#28a745' :
                              order.status === 'SHIPPED' ? '#007bff' :
                              order.status === 'CONFIRMED' ? '#17a2b8' :
                              order.status === 'PENDING' ? '#ffc107' :
                              '#dc3545'
                            }, ${
                              order.status === 'DELIVERED' ? '#20c997' :
                              order.status === 'SHIPPED' ? '#0056b3' :
                              order.status === 'CONFIRMED' ? '#138496' :
                              order.status === 'PENDING' ? '#e0a800' :
                              '#c82333'
                            })`,
                            display: 'flex',
                            alignItems: 'center',
                            justifyContent: 'center'
                          }}
                        >
                          <div className="text-center text-white">
                            <div style={{ fontSize: '2rem', marginBottom: '0.25rem' }}>
                              {order.status === 'DELIVERED' ? '‚úÖ' :
                               order.status === 'SHIPPED' ? 'üöö' :
                               order.status === 'CONFIRMED' ? 'üìã' :
                               order.status === 'PENDING' ? '‚è≥' : '‚ùå'}
                            </div>
                            <Badge bg="light" text="dark" className="fw-bold">
                              {order.status}
                            </Badge>
                          </div>
                        </div>

                        <Card.Body className="d-flex flex-column">
                          <div className="d-flex justify-content-between align-items-start mb-3">
                            <div>
                              <h5 className="mb-1">Order #{order.id}</h5>
                              <small className="text-muted">
                                {formatDate(order.orderDate)}
                              </small>
                            </div>
                            <h4 className="text-success fw-bold mb-0">
                              ${order.totalAmount}
                            </h4>
                          </div>

                          <div className="mb-3">
                            <div className="d-flex justify-content-between text-muted small">
                              <span>Items: {order.items?.length || 0}</span>
                              <span>User ID: {order.userId}</span>
                            </div>
                          </div>

                          <Button
                            variant="outline-primary"
                            className="w-100 mt-auto"
                            onClick={() => handleViewDetails(order)}
                            style={{ transition: 'all 0.2s ease' }}
                          >
                            üëÅÔ∏è View Details
                          </Button>
                        </Card.Body>
                      </Card>
                    </Col>
                  ))}
                </Row>
              </>
            )}
          </>
        )}
      </Container>

      {/* Order Details Modal */}
      <Modal show={showDetails} onHide={() => setShowDetails(false)} size="lg">
        <Modal.Header closeButton>
          <Modal.Title>Order Details #{selectedOrder?.id}</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          {selectedOrder && (
            <div>
              <div className="row mb-3">
                <div className="col-md-6">
                  <strong>Order Date:</strong> {formatDate(selectedOrder.orderDate)}
                </div>
                <div className="col-md-6">
                  <strong>Status:</strong>{' '}
                  <Badge bg={getStatusBadgeVariant(selectedOrder.status)}>
                    {selectedOrder.status}
                  </Badge>
                </div>
              </div>
              <div className="row mb-3">
                <div className="col-md-6">
                  <strong>User ID:</strong> {selectedOrder.userId}
                </div>
                <div className="col-md-6">
                  <strong>Total Amount:</strong>{' '}
                  <span className="fw-bold text-success">${selectedOrder.totalAmount}</span>
                </div>
              </div>

              <h5 className="mt-4 mb-3">Order Items</h5>
              <Table striped bordered>
                <thead>
                  <tr>
                    <th>Product</th>
                    <th>Quantity</th>
                    <th>Price</th>
                    <th>Subtotal</th>
                  </tr>
                </thead>
                <tbody>
                  {selectedOrder.items?.map((item, index) => (
                    <tr key={index}>
                      <td>{item.productName}</td>
                      <td>{item.quantity}</td>
                      <td>${item.price}</td>
                      <td className="fw-bold">${(item.price * item.quantity).toFixed(2)}</td>
                    </tr>
                  ))}
                </tbody>
                <tfoot>
                  <tr>
                    <td colSpan="3" className="text-end fw-bold">Total:</td>
                    <td className="fw-bold text-success">${selectedOrder.totalAmount}</td>
                  </tr>
                </tfoot>
              </Table>
            </div>
          )}
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowDetails(false)}>
            Close
          </Button>
        </Modal.Footer>
      </Modal>
    </Container>
  );
}

export default OrderList;
